// addUnique: Add a value to an array only if it is not already present
function addUnique(arr, value) {
  // Check if value is not already in the array
  if (!arr.includes(value)) {
    // Append the value to the end of the array
    arr.push(value);
    // Return true to indicate the array was modified
    return true;
  }
  // If value was already present, return false (no modification)
  return false;
}

def is_valid_ipv4(addr):
    """Return True if addr is a valid IPv4 address like '192.168.0.1'."""
    parts = addr.split('.')
    if len(parts) != 4:
        return False
    for p in parts:
        if not p.isdigit():
            return False
        n = int(p)
        if n < 0 or n > 255:
            return False
    return True
